import React, { useState } from 'react';
import './FAQ.css';

const FAQ = () => {
  const [activeIndex, setActiveIndex] = useState(null);

  // FAQs refinadas y agrupadas para mejor UX y adaptadas a la boda
  const faqs = [
    {
      category: "Ceremonia & Recepción",
      icon: "fas fa-church",
      questions: [
        {
          question: "¿Dónde y cuándo es la ceremonia?",
          answer: "La ceremonia será el 8 de agosto a las 18:30h en la Iglesia de San Miguel (Mondariz). Por favor, llega 15 minutos antes."
        },
        {
          question: "¿Dónde es la recepción y fiesta?",
          answer: "La recepción y fiesta serán en el Hotel Balneario Mondariz, a 500 metros de la iglesia. La fiesta termina a las 2:00h."
        },
        {
          question: "¿Puedo tomar fotos durante la ceremonia?",
          answer: "Durante el intercambio de votos, por favor no tomes fotos. Después, ¡haz todas las que quieras!"
        },
        {
          question: "¿Habrá menú para niños y opciones especiales?",
          answer: "Sí, hay menú infantil y opciones para alergias/intolerancias. Indícalo en el RSVP."
        },
        {
          question: "¿Qué tipo de música habrá?",
          answer: "DJ profesional con música variada para todos los gustos. ¡Prepárate para bailar!"
        }
      ]
    },
    {
      category: "Vestuario & Dress Code",
      icon: "fas fa-tshirt",
      questions: [
        {
          question: "¿Cuál es el dress code?",
          answer: "Formal/elegante. Hombres: traje y corbata/pajarita. Mujeres: vestido elegante o conjunto formal. Evita blanco y negro total."
        },
        {
          question: "¿Puedo usar zapatos cómodos?",
          answer: "¡Sí! Habrá bailarinas disponibles para quien lo necesite."
        },
        {
          question: "¿Qué colores están bien?",
          answer: "Todos menos blanco y negro total. Pasteles, dorados, azules, rosas, verdes... ¡Brilla!"
        }
      ]
    },
    {
      category: "Transporte & Logística",
      icon: "fas fa-bus",
      questions: [
        {
          question: "¿Hay transporte para invitados?",
          answer: "Sí, habrá shuttles gratuitos desde los hoteles recomendados hasta el evento. Horarios por WhatsApp la semana previa."
        },
        {
          question: "¿Dónde puedo aparcar?",
          answer: "Parking gratuito en el hotel y en la calle."
        },
        {
          question: "¿Qué pasa si llueve?",
          answer: "Hay salones interiores preciosos como plan B. Agosto suele ser soleado, pero estamos preparados."
        }
      ]
    },
    {
      category: "Regalos & Detalles",
      icon: "fas fa-gift",
      questions: [
        {
          question: "¿Cómo funciona la lista de regalos?",
          answer: "Puedes verla en la sección 'Lista de Regalos'. También aceptamos aportaciones para la luna de miel."
        },
        {
          question: "¿Puedo llevar el regalo el día de la boda?",
          answer: "Preferimos online o envío previo, pero habrá mesa especial si lo traes el día de la boda."
        },
        {
          question: "¿Prefieren dinero o regalos?",
          answer: "¡Lo que prefieras! Lo importante es que lo hagas con cariño."
        }
      ]
    },
    {
      category: "Alojamiento",
      icon: "fas fa-bed",
      questions: [
        {
          question: "¿Qué hoteles recomendáis?",
          answer: "Hotel Balneario Mondariz (recomendado), Villa Termal Caldas, Parador de Pontevedra y casas rurales. Ver sección 'Alojamiento'."
        },
        {
          question: "¿Hasta cuándo puedo reservar?",
          answer: "Reserva antes del 1 de julio para asegurar plaza y descuento. Agosto es temporada alta."
        },
        {
          question: "¿Necesito coche para moverme?",
          answer: "No es imprescindible. Hay transporte y todo está cerca."
        }
      ]
    }
  ];

  const toggleAccordion = (categoryIndex, questionIndex) => {
    const index = `${categoryIndex}-${questionIndex}`;
    setActiveIndex(activeIndex === index ? null : index);
  };

  return (
    <div className="faq-page">
      {/* Header */}
      <div className="faq-header">
        <div className="container">
          <h1 className="faq-title">
            <i className="fas fa-question-circle"></i>
            Preguntas Frecuentes
          </h1>
          <p className="faq-subtitle">
            Resolvemos todas tus dudas para que disfrutes al máximo de nuestra celebración
          </p>
        </div>
      </div>

      {/* Content */}
      <div className="faq-content">
        <div className="container">
          
          {/* CTA Hero Section */}
          <div className="cta-hero">
            <div className="cta-content">
              <h2>🤍 8 de Agosto, 2026 🤍</h2>
              <p>Únete a nosotros en el día más especial de nuestras vidas</p>
              <div className="cta-buttons">
                <a href="/rsvp" className="cta-btn primary">
                  <i className="fas fa-heart"></i>
                  Confirmar Asistencia
                </a>
                <a href="/accommodation" className="cta-btn secondary">
                  <i className="fas fa-bed"></i>
                  Reservar Hotel
                </a>
              </div>
            </div>
            <div className="quick-timeline">
              <div className="timeline-item">
                <span className="time">18:30</span>
                <span className="event">Ceremonia</span>
              </div>
              <div className="timeline-item">
                <span className="time">19:30</span>
                <span className="event">Cóctel</span>
              </div>
              <div className="timeline-item">
                <span className="time">21:00</span>
                <span className="event">Cena</span>
              </div>
              <div className="timeline-item">
                <span className="time">23:00</span>
                <span className="event">Fiesta</span>
              </div>
            </div>
          </div>

          {/* FAQ Categories */}
          <div className="faq-categories">
            {faqs.map((category, categoryIndex) => (
              <div key={categoryIndex} className="faq-category">
                <div className="category-header">
                  <i className={category.icon}></i>
                  <h2>{category.category}</h2>
                </div>
                
                <div className="questions-container">
                  {category.questions.map((item, questionIndex) => {
                    const isActive = activeIndex === `${categoryIndex}-${questionIndex}`;
                    return (
                      <div key={questionIndex} className={`faq-item ${isActive ? 'active' : ''}`}>
                        <button
                          className="faq-question"
                          onClick={() => toggleAccordion(categoryIndex, questionIndex)}
                        >
                          <span>{item.question}</span>
                          <i className={`fas fa-chevron-${isActive ? 'up' : 'down'}`}></i>
                        </button>
                        <div className={`faq-answer ${isActive ? 'show' : ''}`}>
                          <p>{item.answer}</p>
                        </div>
                      </div>
                    );
                  })}
                </div>
              </div>
            ))}
          </div>

          {/* Contact Section */}
          <div className="faq-contact">
            <div className="contact-card glass">
              <h3><i className="fas fa-comments"></i> ¿No encuentras la respuesta?</h3>
              <p>No dudes en contactarnos directamente. Estamos aquí para ayudarte con cualquier duda que tengas.</p>
              <div className="contact-options">
                <a href="mailto:dani.ana.boda@example.com" className="contact-btn">
                  <i className="fas fa-envelope"></i>
                  Enviar email
                </a>
                <a href="https://wa.me/34123456789" className="contact-btn" target="_blank" rel="noopener noreferrer">
                  <i className="fab fa-whatsapp"></i>
                  WhatsApp
                </a>
                <a href="tel:+34123456789" className="contact-btn">
                  <i className="fas fa-phone"></i>
                  Llamar
                </a>
              </div>
            </div>
          </div>

          {/* Tips Section */}
          <div className="tips-section">
            <h2 className="section-title">💡 Consejos para el día de la boda</h2>
            <div className="tips-grid">
              <div className="tip-card glass">
                <i className="fas fa-sun"></i>
                <h4>Para el clima</h4>
                <p>Agosto suele ser caluroso. Trae protector solar, un abanico bonito y quizás una chaqueta ligera para la noche.</p>
              </div>
              <div className="tip-card glass">
                <i className="fas fa-camera"></i>
                <h4>Para las fotos</h4>
                <p>Tendremos fotógrafo profesional, pero siéntete libre de hacer fotos durante el cóctel y la fiesta. ¡Usa nuestro hashtag!</p>
              </div>
              <div className="tip-card glass">
                <i className="fas fa-heart"></i>
                <h4>Para disfrutar</h4>
                <p>¡Relájate y diviértete! Es nuestro día especial, pero queremos que tú también lo pases genial. ¡Baila, ríe y celebra con nosotros!</p>
              </div>
              <div className="tip-card glass">
                <i className="fas fa-utensils"></i>
                <h4>Para la comida</h4>
                <p>Habrá cóctel con aperitivos, cena completa y barra libre toda la noche. Si tienes alergias, asegúrate de indicarlo en el RSVP.</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FAQ;
