function skinBtnStyle(active) {
  return {
    background: active ? '#111827' : '#f3f4f6',
    color: active ? 'white' : '#111827',
    border: '1px solid ' + (active ? '#111827' : '#e5e7eb'),
    padding: '.6rem 1rem',
    borderRadius: 10,
    cursor: 'pointer',
    minWidth: 120,
    fontWeight: 600
  };
}
function spawnObstacle(w) {
  // Dos tipos: low (bloque en el suelo) y high (barra elevada)
  const type = Math.random() < 0.55 ? 'low' : 'high';
  if (type === 'low') {
    const width = 26 + Math.random() * 16;
    const height = 22 + Math.random() * 20;
    const x = w.worldX + 900 + Math.random() * 80;
    const y = w.groundY - height;
    w.obstacles.push({
      type,
      x,
      y,
      w: width,
      h: height,
      scored: false,
      screenX: 0
    });
  } else {
    const width = 80 + Math.random() * 60;
    const height = 18;
    const x = w.worldX + 900 + Math.random() * 80;
    // altura que obliga a agacharse (por encima de altura de pie)
    const y = w.groundY - (42 + 12);
    w.obstacles.push({
      type,
      x,
      y,
      w: width,
      h: height,
      scored: false,
      screenX: 0
    });
  }
}
function formatTime(t) {
  const s = Math.max(0, Math.floor(t));
  const mm = String(Math.floor(s / 60)).padStart(2, '0');
  const ss = String(s % 60).padStart(2, '0');
  return `${mm}:${ss}`;
}
function drawObstacleLow(ctx, x, y, w, h) {
  ctx.save();
  ctx.fillStyle = '#94a3b8';
  ctx.strokeStyle = '#475569';
  ctx.lineWidth = 2;
  roundRect(ctx, x, y, w, h, 6);
  ctx.fill();
  ctx.stroke();
  ctx.restore();
}
function drawObstacleHigh(ctx, x, y, w, h) {
  ctx.save();
  // postes laterales
  ctx.fillStyle = '#9ca3af';
  roundRect(ctx, x, y + h - 30, 4, 30, 2);
  roundRect(ctx, x + w - 4, y + h - 30, 4, 30, 2);
  ctx.fill();
  // barra (verde temÃ¡tica)
  ctx.fillStyle = '#22c55e';
  roundRect(ctx, x, y, w, h, 6);
  ctx.fill();
  ctx.restore();
}
function drawGroom(ctx, x, y, w, h, jumping, crouching, skin) {
  ctx.save();
  if (skin === 'novia') {
    // vestido
    ctx.fillStyle = '#f8fafc';
    roundRect(ctx, x - 2, y + h * 0.35, w + 4, h * 0.65, 10);
    ctx.fill();
    // torso
    ctx.fillStyle = '#e5e7eb';
    roundRect(ctx, x, y, w, h * 0.45, 8);
    ctx.fill();
    // cabeza
    ctx.fillStyle = '#fde68a';
    ctx.beginPath();
    const headY = crouching ? y - 6 : y - 8;
    ctx.arc(x + w / 2, headY, 10, 0, Math.PI * 2);
    ctx.fill();
    // velo
    ctx.globalAlpha = 0.6;
    ctx.fillStyle = '#e0f2fe';
    roundRect(ctx, x - 6, y - 6, w + 12, 16, 8);
    ctx.fill();
    ctx.globalAlpha = 1;
  } else {
    // traje
    ctx.fillStyle = '#1f2937';
    roundRect(ctx, x, y, w, h, 8);
    ctx.fill();
    // cabeza
    ctx.fillStyle = '#fde68a';
    ctx.beginPath();
    const headY = crouching ? y - 6 : y - 8;
    ctx.arc(x + w / 2, headY, 10, 0, Math.PI * 2);
    ctx.fill();
    // corbata
    ctx.fillStyle = '#ef4444';
    ctx.fillRect(x + w / 2 - 3, y + 10, 6, 10);
  }
  // sombra al saltar
  if (jumping) {
    ctx.globalAlpha = 0.2;
    ctx.fillStyle = '#000';
    ctx.beginPath();
    ctx.ellipse(x + w / 2, y + h + 10, w * 0.6, 4, 0, 0, Math.PI * 2);
    ctx.fill();
    ctx.globalAlpha = 1;
  }
  ctx.restore();
}
function drawStoryMoment(ctx, canvas, momentText) {
  ctx.save();

  // Fondo semitransparente para el texto
  ctx.fillStyle = 'rgba(255, 255, 255, 0.85)';
  const textWidth = ctx.measureText(momentText).width + 40;
  const boxHeight = 50;
  const x = (canvas.width - textWidth) / 2;
  const y = 20;
  roundRect(ctx, x, y, textWidth, boxHeight, 12);
  ctx.fill();

  // Borde sutil
  ctx.strokeStyle = 'rgba(99, 102, 241, 0.3)';
  ctx.lineWidth = 2;
  ctx.stroke();

  // Texto del momento
  ctx.fillStyle = '#1f2937';
  ctx.font = 'bold 14px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  ctx.textAlign = 'center';
  ctx.fillText(momentText, canvas.width / 2, y + boxHeight / 2 + 5);
  ctx.restore();
}
function drawStoryBackground(ctx, canvas, momentText) {
  console.log('drawStoryBackground llamado con:', momentText);
  ctx.save();

  // Crear un efecto de fondo grande y sutil
  ctx.globalAlpha = 0.15;

  // Fondo degradado temÃ¡tico para el momento
  const gradient = ctx.createRadialGradient(canvas.width / 2, canvas.height / 2, 0, canvas.width / 2, canvas.height / 2, canvas.width * 0.8);
  gradient.addColorStop(0, '#fbbf24'); // dorado
  gradient.addColorStop(0.5, '#f59e0b'); // oro mÃ¡s intenso
  gradient.addColorStop(1, 'transparent');
  ctx.fillStyle = gradient;
  ctx.fillRect(0, 0, canvas.width, canvas.height);

  // Texto del momento como marca de agua en el fondo
  ctx.globalAlpha = 0.25;
  ctx.fillStyle = '#92400e';
  ctx.font = 'bold 36px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  ctx.textAlign = 'center';
  ctx.textBaseline = 'middle';

  // Dividir el texto en lÃ­neas si es muy largo
  const words = momentText.split(' ');
  let lines = [];
  let currentLine = '';
  for (const word of words) {
    const testLine = currentLine + (currentLine ? ' ' : '') + word;
    const metrics = ctx.measureText(testLine);
    if (metrics.width > canvas.width * 0.7 && currentLine) {
      lines.push(currentLine);
      currentLine = word;
    } else {
      currentLine = testLine;
    }
  }
  if (currentLine) lines.push(currentLine);

  // Dibujar lÃ­neas centradas
  const lineHeight = 40;
  const startY = canvas.height / 2 - (lines.length - 1) * lineHeight / 2;
  for (let i = 0; i < lines.length; i++) {
    ctx.fillText(lines[i], canvas.width / 2, startY + i * lineHeight);
  }

  // PequeÃ±a caja informativa arriba
  ctx.globalAlpha = 0.9;
  ctx.fillStyle = 'rgba(251, 191, 36, 0.9)';
  const boxWidth = 300;
  const boxHeight = 35;
  const boxX = (canvas.width - boxWidth) / 2;
  const boxY = 15;
  roundRect(ctx, boxX, boxY, boxWidth, boxHeight, 8);
  ctx.fill();

  // Texto de la caja
  ctx.fillStyle = '#92400e';
  ctx.font = 'bold 12px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  ctx.textAlign = 'center';
  ctx.textBaseline = 'middle';
  ctx.fillText('ðŸ’• Momento especial de Ana y Dani ðŸ’•', canvas.width / 2, boxY + boxHeight / 2);
  ctx.restore();
}
function drawIntro(ctx, canvas, lines, offset) {
  ctx.save();
  ctx.fillStyle = '#10b981'; // verde
  ctx.textAlign = 'center';
  ctx.font = 'bold 22px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  const centerX = canvas.width / 2;
  let y = canvas.height + offset;
  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    const scale = 1 - i * 0.05; // ligera perspectiva
    ctx.save();
    ctx.translate(centerX, y);
    ctx.scale(scale, scale);
    ctx.fillText(line, 0, 0);
    ctx.restore();
    y += 36;
  }
  ctx.restore();
}
function drawOverlay(ctx, canvas, title, subtitle) {
  ctx.save();
  ctx.fillStyle = 'rgba(0,0,0,0.45)';
  ctx.fillRect(0, 0, canvas.width, canvas.height);
  ctx.fillStyle = '#fff';
  ctx.font = 'bold 22px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  ctx.textAlign = 'center';
  ctx.fillText(title, canvas.width / 2, canvas.height / 2 - 6);
  ctx.font = '14px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  ctx.fillText(subtitle, canvas.width / 2, canvas.height / 2 + 18);
  ctx.restore();
}
function roundRect(ctx, x, y, w, h, r) {
  ctx.beginPath();
  ctx.moveTo(x + r, y);
  ctx.arcTo(x + w, y, x + w, y + h, r);
  ctx.arcTo(x + w, y + h, x, y + h, r);
  ctx.arcTo(x, y + h, x, y, r);
  ctx.arcTo(x, y, x + w, y, r);
  ctx.closePath();
}
