import React, { useEffect, useRef, useState } from 'react';

// Minijuego 2D: El novio/la novia quiere llegar a la iglesia esquivando obstÃ¡culos
// Controles: Space o Flecha â†‘ para saltar. MantÃ©n Flecha â†“ para agacharte (en el aire: caÃ­da rÃ¡pida). R para reiniciar.
export default function Game() {
  const containerRef = useRef(null);
  const canvasRef = useRef(null);

  // Estados de juego: menu | intro | playing | win | gameover
  const [status, setStatus] = useState('menu');
  const [skin, setSkin] = useState('novio'); // 'novio' | 'novia'
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(300); // 5 minutos

  const GAME_DURATION = 300; // 5 min gameplay (la intro no consume tiempo)

  // NUEVO: refs para evitar valores congelados en el canvas
  const SCORE = useRef(0);
  const TIMELEFT = useRef(GAME_DURATION);
  // NUEVO: mostrar la intro solo una vez
  const INTRO_SHOWN = useRef(false);

  // Estado de mute para el audio
  const [muted, setMuted] = useState(false);

  // Intro tipo "Star Wars"
  const INTRO = useRef({
    offset: 0,
    speed: 60, // px/s desplazamiento hacia arriba
    lines: [
      'En una galaxia muy, muy cercana...',
      'Dos estrellas destinadas a unirse brillan con fuerza.',
      'El gran dÃ­a ha llegado y todos esperan en la iglesia.',
      'Ayuda a la pareja a llegar a tiempo sorteando obstÃ¡culos...',
      'Controles: â†‘ o ESPACIO para saltar, â†“ para agacharte o caer rÃ¡pido.',
      'Â¡Que la fuerza del amor te acompaÃ±e!'
    ],
  });

  // Momentos clave de la pareja (cada 30 segundos)
  const STORY_MOMENTS = useRef([
    { text: "Ana y Dani en la Facultad de Derecho de la USC ðŸ›ï¸", icon: "ðŸ“š", timeStart: 0 },
    { text: "Ana preparando las oposiciones para Jueza âš–ï¸", icon: "ðŸ‘©â€âš–ï¸", timeStart: 30 },
    { text: "Dani cursando el MÃ¡ster de AbogacÃ­a ðŸ“–", icon: "ðŸŽ“", timeStart: 60 },
    { text: "Dani empezando a trabajar como abogado ðŸ’¼", icon: "ðŸ‘¨â€ðŸ’¼", timeStart: 90 },
    { text: "Dani trabajando en PorriÃ±o ðŸ¢", icon: "âš–ï¸", timeStart: 120 },
    { text: "Dani mudÃ¡ndose a CarballiÃ±o ðŸ ", icon: "ðŸ“¦", timeStart: 150 },
    { text: "Ana trabajando en Ponteareas ðŸ›ï¸", icon: "ðŸ‘©â€ðŸ’¼", timeStart: 180 },
    { text: "Ana aprobando la oposiciÃ³n en Pontevedra! ðŸŽ‰", icon: "ðŸ†", timeStart: 210 },
    { text: "Ana y Dani viviendo juntos en Pontevedra ðŸ’•", icon: "ðŸ¡", timeStart: 240 },
    { text: "La pedida de mano en la Torre de HÃ©rcules ðŸ’", icon: "ðŸ’’", timeStart: 270 },
    { text: "Â¡Y hoy... la boda! ðŸ‘°ðŸ’’ðŸ¤µ", icon: "ðŸ’–", timeStart: 300 }
  ]);

  const [currentStoryMoment, setCurrentStoryMoment] = useState(null);

  // Estado del mundo
  const WORLD = useRef({
    speed: 220,
    maxSpeed: 380,
    gravity: 1700,
    jumpV: 620,
    fastFallBoost: 2200,
    groundY: 220,
    player: { x: 80, y: 0, w: 32, h: 42, vy: 0, onGround: true, crouching: false },
    worldX: 0,
    obstacles: [],
    startTs: 0,
    lastTs: 0,
    spawnTimer: 0,
    spawnInterval: 1.4,
  });

  // Reset base (sin cambiar estado)
  const resetWorld = () => {
    setScore(0);
    setTimeLeft(GAME_DURATION);
    setCurrentStoryMoment(null); // resetear momento de la historia
    WORLD.current.worldX = 0;
    WORLD.current.lastTs = 0;
    WORLD.current.startTs = 0;
    WORLD.current.speed = 220;
    WORLD.current.spawnInterval = 1.4;
    WORLD.current.spawnTimer = 0.8;
    WORLD.current.obstacles = [];
    WORLD.current.player = { x: 80, y: WORLD.current.groundY - 42, w: 32, h: 42, vy: 0, onGround: true, crouching: false };
    // NUEVO: sincronizar refs
    SCORE.current = 0;
    TIMELEFT.current = GAME_DURATION;
  };

  useEffect(() => {
    // init: mostrar menÃº
    resetWorld();
    INTRO.current.offset = 320; // arranca fuera de la pantalla por abajo
  }, []);

  // Bucle de juego y render
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext('2d');

    let rafId;
    const loop = (ts) => {
      if (!WORLD.current.lastTs) {
        WORLD.current.lastTs = ts;
        if (!WORLD.current.startTs && status === 'playing') WORLD.current.startTs = ts;
      }
      const dt = Math.min(0.032, (ts - WORLD.current.lastTs) / 1000);
      WORLD.current.lastTs = ts;

      update(dt, ts);
      render(ctx, canvas);
      rafId = requestAnimationFrame(loop);
    };

    rafId = requestAnimationFrame(loop);
    return () => cancelAnimationFrame(rafId);
  }, [status]);

  // NUEVO: cada vez que entramos en intro, reiniciar offset para que se vea siempre
  useEffect(() => {
    if (status === 'intro') {
      INTRO.current.offset = 320;
    }
  }, [status]);

  // Entradas
  useEffect(() => {
    const onKeyDown = (e) => {
      if (e.repeat) return;
      if ((e.code === 'Space' || e.code === 'ArrowUp') && status === 'playing') jump();
      if (e.code === 'ArrowDown') {
        if (status === 'playing') crouch(true);
      }
      if (e.code === 'KeyR') {
        if (status === 'playing' || status === 'gameover' || status === 'win') {
          resetWorld();
          setStatus('menu');
        }
      }
    };
    const onKeyUp = (e) => {
      if (e.code === 'ArrowDown') crouch(false);
    };
    const onClick = () => { if (status === 'playing') jump(); };

    window.addEventListener('keydown', onKeyDown);
    window.addEventListener('keyup', onKeyUp);
    window.addEventListener('touchstart', onClick, { passive: true });
    window.addEventListener('mousedown', onClick);
    return () => {
      window.removeEventListener('keydown', onKeyDown);
      window.removeEventListener('keyup', onKeyUp);
      window.removeEventListener('touchstart', onClick);
      window.removeEventListener('mousedown', onClick);
    };
  }, [status]);

  const jump = () => {
    const p = WORLD.current.player;
    if (p.onGround) {
      p.vy = -WORLD.current.jumpV;
      p.onGround = false;
    }
  };

  const crouch = (on) => {
    const p = WORLD.current.player;
    if (on) {
      if (!p.onGround) {
        p.vy += WORLD.current.fastFallBoost * 0.04; // caÃ­da rÃ¡pida
      } else if (!p.crouching) {
        p.crouching = true;
        const prevBottom = p.y + p.h;
        p.h = 26;
        p.y = prevBottom - p.h;
      }
    } else {
      if (p.crouching) {
        const prevBottom = p.y + p.h;
        p.h = 42;
        p.y = prevBottom - p.h;
        p.crouching = false;
      }
    }
  };

  // Acciones de UI
  const handlePlayFullscreen = async () => {
    try {
      if (containerRef.current?.requestFullscreen) {
        await containerRef.current.requestFullscreen();
      }
    } catch (e) { /* noop */ }
    // Limpiar mundo antes de jugar
    resetWorld();

    // Iniciar mÃºsica en la interacciÃ³n de usuario
    const audioEl = document.getElementById('bg-music');
    if (audioEl) {
      audioEl.currentTime = 0;
      audioEl.play();
      audioEl.muted = muted;
    }

    if (!INTRO_SHOWN.current) {
      INTRO.current.offset = 320; // preparar intro
      setStatus('intro');
      INTRO_SHOWN.current = true; // marcar intro mostrada
    } else {
      startPlaying();
    }
  };

  const startPlaying = () => {
    // Reset de marcas de tiempo para que el cronÃ³metro cuente desde ahora
    WORLD.current.lastTs = 0;
    WORLD.current.startTs = 0;
    setStatus('playing');
  };

  // NUEVO: reintentar sin repetir la intro
  const handleRetry = () => {
    resetWorld();
    setCurrentStoryMoment(null); // resetear momento de la historia
    startPlaying();
  };

  const update = (dt, ts) => {
    if (status === 'menu') return; // nada

    if (status === 'intro') {
      INTRO.current.offset -= INTRO.current.speed * dt;
      // tras ~7-8s o cuando el texto sale por arriba, comenzamos
      if (INTRO.current.offset < -120) startPlaying();
      return;
    }

    if (status !== 'playing') return;

    // Tiempo de juego
    if (WORLD.current.startTs === 0) WORLD.current.startTs = ts;
    const elapsed = (ts - WORLD.current.startTs) / 1000;
    const remaining = Math.max(0, GAME_DURATION - elapsed);
    // setTimeLeft(remaining);
    TIMELEFT.current = remaining; // usar ref para el HUD del canvas
    
    // Actualizar momento de la historia cada 30 segundos
    let currentMoment = null;
    
    // Siempre mostrar el primer momento al inicio
    if (elapsed >= 0) {
      currentMoment = STORY_MOMENTS.current[0].text;
      
      // Buscar si hay un momento mÃ¡s especÃ­fico para este tiempo
      for (let i = 1; i < STORY_MOMENTS.current.length; i++) {
        const moment = STORY_MOMENTS.current[i];
        if (elapsed >= moment.timeStart) {
          currentMoment = moment.text;
        } else {
          break; // Ya encontramos el momento correcto
        }
      }
    }
    
    if (currentMoment !== currentStoryMoment) {
      setCurrentStoryMoment(currentMoment);
      console.log(`Momento actualizado: "${currentMoment}" en segundo ${Math.floor(elapsed)}`);
    }
    
    if (remaining <= 0.01) {
      setStatus('win');
      return;
    }

    const w = WORLD.current;
    const p = w.player;

    // AceleraciÃ³n progresiva (cada vez mÃ¡s rÃ¡pido)
    const tFactor = Math.min(1, elapsed / GAME_DURATION);
    w.speed = 220 + (w.maxSpeed - 220) * tFactor;
    w.spawnInterval = 1.4 - 0.8 * tFactor;

    // Avance del "mundo"
    w.worldX += w.speed * dt;

    // FÃ­sica del jugador
    p.vy += w.gravity * dt;
    p.y += p.vy * dt;

    // Suelo
    const groundY = w.groundY - p.h;
    if (p.y >= groundY) {
      p.y = groundY;
      p.vy = 0;
      p.onGround = true;
    } else {
      p.onGround = false;
    }

    // Spawning de obstÃ¡culos
    w.spawnTimer -= dt;
    if (w.spawnTimer <= 0) {
      spawnObstacle(w);
      w.spawnTimer = w.spawnInterval * (0.9 + Math.random() * 0.4);
    }

    // Colisiones + puntuaciÃ³n
    for (const o of w.obstacles) {
      const sx = o.x - w.worldX + p.x;
      o.screenX = sx;

      if (!o.scored && sx + o.w < p.x) {
        o.scored = true;
        // setScore((s) => s + 100);
        SCORE.current += 100;
      }

      const px1 = p.x, px2 = p.x + p.w;
      const py1 = p.y, py2 = p.y + p.h;
      const ox1 = sx, ox2 = sx + o.w;
      const oy1 = o.y, oy2 = o.y + o.h;
      const collide = px1 < ox2 && px2 > ox1 && py1 < oy2 && py2 > oy1;
      if (collide) {
        setStatus('gameover');
        break;
      }
    }

    // Limpiar obstÃ¡culos fuera de pantalla
    w.obstacles = w.obstacles.filter((o) => o.screenX > -120);

    // PuntuaciÃ³n por distancia
    // setScore((s) => s + Math.floor(w.speed * dt * 0.5));
    SCORE.current += Math.floor(w.speed * dt * 0.5);
  };

  const render = (ctx, canvas) => {
    const w = WORLD.current;
    const p = w.player;

    // FONDO base (cielo) o negro en intro
    ctx.clearRect(0, 0, canvas.width, canvas.height);
    if (status === 'intro') {
      ctx.fillStyle = '#000';
      ctx.fillRect(0, 0, canvas.width, canvas.height);
      drawIntro(ctx, canvas, INTRO.current.lines, INTRO.current.offset);
      return;
    }

    // Fondo cielo
    const g = ctx.createLinearGradient(0, 0, 0, canvas.height);
    g.addColorStop(0, '#e8f0ff');
    g.addColorStop(1, '#ffffff');
    ctx.fillStyle = g;
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Mostrar momento de la historia como elemento de fondo si estÃ¡ jugando
    if (status === 'playing' && currentStoryMoment) {
      drawStoryBackground(ctx, canvas, currentStoryMoment);
    }

    // Suelo
    ctx.fillStyle = '#c7d2fe';
    ctx.fillRect(0, w.groundY + 10, canvas.width, 4);

    // ObstÃ¡culos
    for (const o of w.obstacles) {
      if (o.screenX < -60 || o.screenX > canvas.width + 120) continue;
      if (o.type === 'low') drawObstacleLow(ctx, o.screenX, o.y, o.w, o.h);
      else drawObstacleHigh(ctx, o.screenX, o.y, o.w, o.h);
    }

    // Jugador
    drawGroom(ctx, p.x, p.y, p.w, p.h, !p.onGround, p.crouching, skin);

    // HUD
    if (status === 'playing') {
      ctx.fillStyle = '#334155';
      ctx.font = '12px Inter, system-ui, -apple-system, Segoe UI, Roboto';
      ctx.fillText(`Puntos: ${Math.floor(SCORE.current)}`, 12, 18);
      ctx.fillText(`Tiempo: ${formatTime(TIMELEFT.current)}`, 12, 32);
      
      // Debug: mostrar tiempo transcurrido y momento actual
      const elapsed = WORLD.current.startTs ? (WORLD.current.lastTs - WORLD.current.startTs) / 1000 : 0;
      ctx.fillText(`Transcurrido: ${Math.floor(elapsed)}s`, 12, 46);
      ctx.fillText(`currentStoryMoment: ${currentStoryMoment ? 'SÃ' : 'NO'}`, 12, 60);
      ctx.fillText(`status: ${status}`, 12, 74);
      ctx.fillText(`startTs: ${WORLD.current.startTs ? 'SET' : 'NO'}`, 12, 88);
      if (currentStoryMoment) {
        ctx.fillText(`Momento: ${currentStoryMoment.substring(0, 20)}...`, 12, 102);
      }
    }

    if (status === 'gameover') {
      drawOverlay(ctx, canvas, 'Â¡Ups! Te has tropezado', 'Pulsa R para volver al menÃº');
    } else if (status === 'win') {
      drawOverlay(ctx, canvas, `Â¡Llegaste a tiempo! ðŸ’’`, `PuntuaciÃ³n: ${Math.floor(SCORE.current)}. Pulsa R para volver al menÃº`);
    }
  };

  // BotÃ³n mute para audio HTML5
  const toggleMute = () => {
    const audioEl = document.getElementById('bg-music');
    if (audioEl) {
      audioEl.muted = !audioEl.muted;
      setMuted(audioEl.muted);
    } else {
      setMuted((m) => !m);
    }
  };

  // Limpieza: pausa mÃºsica al desmontar
  useEffect(() => {
    return () => {
      const audioEl = document.getElementById('bg-music');
      if (audioEl) audioEl.pause();
    };
  }, []);

  return (
    <div ref={containerRef} style={{ maxWidth: 860, margin: '0 auto', padding: '1rem', position: 'relative' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '.5rem' }}>Camino a la iglesia</h2>
      {/* Panel de control/menÃº */}
      {status === 'menu' && (
        <div style={{
          position: 'absolute', inset: 0, display: 'flex', alignItems: 'center', justifyContent: 'center',
          background: 'linear-gradient(135deg, rgba(255,255,255,.95), rgba(238,242,255,.95))',
          zIndex: 5, borderRadius: 12
        }}>
          <div style={{ width: 'min(92%, 560px)', padding: '1.25rem', borderRadius: 12, boxShadow: '0 8px 28px rgba(0,0,0,.18)', background: 'white' }}>
            <h3 style={{ marginTop: 0, textAlign: 'center' }}>Elige tu personaje</h3>
            <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', marginBottom: '1rem' }}>
              <button
                onClick={() => setSkin('novio')}
                className={skin === 'novio' ? 'active' : ''}
                style={skinBtnStyle(skin === 'novio')}
              >
                ðŸ¤µ Novio
              </button>
              <button
                onClick={() => setSkin('novia')}
                className={skin === 'novia' ? 'active' : ''}
                style={skinBtnStyle(skin === 'novia')}
              >
                ðŸ‘° Novia
              </button>
            </div>
            <p style={{ textAlign: 'center', color: '#64748b', marginTop: 0 }}>
              Llega a la iglesia esquivando obstÃ¡culos en 5 minutos. El juego acelerarÃ¡ poco a poco.
            </p>
            <div style={{ display: 'flex', justifyContent: 'center' }}>
              <button onClick={handlePlayFullscreen} style={{
                background: 'linear-gradient(135deg, #6366f1, #8b5cf6)', color: 'white', border: 0, padding: '.75rem 1.25rem',
                borderRadius: 10, fontWeight: 700, cursor: 'pointer', boxShadow: '0 6px 18px rgba(99,102,241,.35)'
              }}>â–¶ Jugar en pantalla completa</button>
            </div>
          </div>
        </div>
      )}

      <div style={{ borderRadius: 12, overflow: 'hidden', boxShadow: '0 6px 24px rgba(0,0,0,.12)', background: 'white', position: 'relative' }}>
        {/* Audio de fondo personalizado */}
        <audio
          id="bg-music"
          src="/musica-boda.mp3"
          loop
          autoPlay={false}
          style={{ display: 'none' }}
        />
        {/* BotÃ³n de Mute */}
        <button onClick={toggleMute} aria-label={muted ? 'Activar sonido' : 'Silenciar sonido'}
          style={{ position: 'absolute', right: 8, top: 8, zIndex: 8, background: 'rgba(0,0,0,.55)', color: '#fff', border: 0, borderRadius: 999, width: 36, height: 36, cursor: 'pointer' }}>
          {muted ? 'ðŸ”‡' : 'ðŸ”Š'}
        </button>
        <canvas
          ref={canvasRef}
          width={800}
          height={260}
          style={{ width: '100%', display: 'block', background: status === 'intro' ? '#000' : 'linear-gradient(#eef2ff, #fff)' }}
        />
        {/* NUEVO: overlay con botÃ³n Reintentar al terminar */}
        {(status === 'gameover' || status === 'win') && (
          <div style={{ position: 'absolute', inset: 0, display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 7 }}>
            <div style={{ background: 'rgba(17,24,39,.88)', color: '#fff', padding: '1rem 1.25rem', borderRadius: 12, boxShadow: '0 8px 28px rgba(0,0,0,.35)', textAlign: 'center' }}>
              <div style={{ fontWeight: 700, fontSize: 18, marginBottom: 8 }}>
                {status === 'win' ? 'Â¡Llegaste a tiempo! ðŸ’’' : 'Â¡Ups! Te has tropezado'}
              </div>
              <div style={{ fontSize: 14, opacity: .9, marginBottom: 12 }}>
                PuntuaciÃ³n: {Math.floor(SCORE.current)}
              </div>
              <button onClick={handleRetry} style={{ background: '#22c55e', color: '#0b1a0f', border: 0, padding: '.5rem 1rem', borderRadius: 10, fontWeight: 700, cursor: 'pointer' }}>â†» Reintentar</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

function skinBtnStyle(active) {
  return {
    background: active ? '#111827' : '#f3f4f6',
    color: active ? 'white' : '#111827',
    border: '1px solid ' + (active ? '#111827' : '#e5e7eb'),
    padding: '.6rem 1rem',
    borderRadius: 10,
    cursor: 'pointer',
    minWidth: 120,
    fontWeight: 600,
  };
}

function spawnObstacle(w) {
  // Dos tipos: low (bloque en el suelo) y high (barra elevada)
  const type = Math.random() < 0.55 ? 'low' : 'high';
  if (type === 'low') {
    const width = 26 + Math.random() * 16;
    const height = 22 + Math.random() * 20;
    const x = w.worldX + 900 + Math.random() * 80;
    const y = w.groundY - height;
    w.obstacles.push({ type, x, y, w: width, h: height, scored: false, screenX: 0 });
  } else {
    const width = 80 + Math.random() * 60;
    const height = 18;
    const x = w.worldX + 900 + Math.random() * 80;
    // altura que obliga a agacharse (por encima de altura de pie)
    const y = w.groundY - (42 + 12);
    w.obstacles.push({ type, x, y, w: width, h: height, scored: false, screenX: 0 });
  }
}

function formatTime(t) {
  const s = Math.max(0, Math.floor(t));
  const mm = String(Math.floor(s / 60)).padStart(2, '0');
  const ss = String(s % 60).padStart(2, '0');
  return `${mm}:${ss}`;
}

function drawObstacleLow(ctx, x, y, w, h) {
  ctx.save();
  ctx.fillStyle = '#94a3b8';
  ctx.strokeStyle = '#475569';
  ctx.lineWidth = 2;
  roundRect(ctx, x, y, w, h, 6);
  ctx.fill();
  ctx.stroke();
  ctx.restore();
}

function drawObstacleHigh(ctx, x, y, w, h) {
  ctx.save();
  // postes laterales
  ctx.fillStyle = '#9ca3af';
  roundRect(ctx, x, y + h - 30, 4, 30, 2);
  roundRect(ctx, x + w - 4, y + h - 30, 4, 30, 2);
  ctx.fill();
  // barra (verde temÃ¡tica)
  ctx.fillStyle = '#22c55e';
  roundRect(ctx, x, y, w, h, 6);
  ctx.fill();
  ctx.restore();
}

function drawGroom(ctx, x, y, w, h, jumping, crouching, skin) {
  ctx.save();
  if (skin === 'novia') {
    // vestido
    ctx.fillStyle = '#f8fafc';
    roundRect(ctx, x - 2, y + h * 0.35, w + 4, h * 0.65, 10);
    ctx.fill();
    // torso
    ctx.fillStyle = '#e5e7eb';
    roundRect(ctx, x, y, w, h * 0.45, 8);
    ctx.fill();
    // cabeza
    ctx.fillStyle = '#fde68a';
    ctx.beginPath();
    const headY = crouching ? y - 6 : y - 8;
    ctx.arc(x + w / 2, headY, 10, 0, Math.PI * 2);
    ctx.fill();
    // velo
    ctx.globalAlpha = 0.6;
    ctx.fillStyle = '#e0f2fe';
    roundRect(ctx, x - 6, y - 6, w + 12, 16, 8);
    ctx.fill();
    ctx.globalAlpha = 1;
  } else {
    // traje
    ctx.fillStyle = '#1f2937';
    roundRect(ctx, x, y, w, h, 8);
    ctx.fill();
    // cabeza
    ctx.fillStyle = '#fde68a';
    ctx.beginPath();
    const headY = crouching ? y - 6 : y - 8;
    ctx.arc(x + w / 2, headY, 10, 0, Math.PI * 2);
    ctx.fill();
    // corbata
    ctx.fillStyle = '#ef4444';
    ctx.fillRect(x + w / 2 - 3, y + 10, 6, 10);
  }
  // sombra al saltar
  if (jumping) {
    ctx.globalAlpha = 0.2;
    ctx.fillStyle = '#000';
    ctx.beginPath();
    ctx.ellipse(x + w / 2, y + h + 10, w * 0.6, 4, 0, 0, Math.PI * 2);
    ctx.fill();
    ctx.globalAlpha = 1;
  }
  ctx.restore();
}

function drawStoryMoment(ctx, canvas, momentText) {
  ctx.save();
  
  // Fondo semitransparente para el texto
  ctx.fillStyle = 'rgba(255, 255, 255, 0.85)';
  const textWidth = ctx.measureText(momentText).width + 40;
  const boxHeight = 50;
  const x = (canvas.width - textWidth) / 2;
  const y = 20;
  
  roundRect(ctx, x, y, textWidth, boxHeight, 12);
  ctx.fill();
  
  // Borde sutil
  ctx.strokeStyle = 'rgba(99, 102, 241, 0.3)';
  ctx.lineWidth = 2;
  ctx.stroke();
  
  // Texto del momento
  ctx.fillStyle = '#1f2937';
  ctx.font = 'bold 14px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  ctx.textAlign = 'center';
  ctx.fillText(momentText, canvas.width / 2, y + boxHeight / 2 + 5);
  
  ctx.restore();
}

function drawStoryBackground(ctx, canvas, momentText) {
  console.log('drawStoryBackground llamado con:', momentText);
  ctx.save();
  
  // Crear un efecto de fondo grande y sutil
  ctx.globalAlpha = 0.15;
  
  // Fondo degradado temÃ¡tico para el momento
  const gradient = ctx.createRadialGradient(
    canvas.width / 2, canvas.height / 2, 0,
    canvas.width / 2, canvas.height / 2, canvas.width * 0.8
  );
  gradient.addColorStop(0, '#fbbf24'); // dorado
  gradient.addColorStop(0.5, '#f59e0b'); // oro mÃ¡s intenso
  gradient.addColorStop(1, 'transparent');
  
  ctx.fillStyle = gradient;
  ctx.fillRect(0, 0, canvas.width, canvas.height);
  
  // Texto del momento como marca de agua en el fondo
  ctx.globalAlpha = 0.25;
  ctx.fillStyle = '#92400e';
  ctx.font = 'bold 36px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  ctx.textAlign = 'center';
  ctx.textBaseline = 'middle';
  
  // Dividir el texto en lÃ­neas si es muy largo
  const words = momentText.split(' ');
  let lines = [];
  let currentLine = '';
  
  for (const word of words) {
    const testLine = currentLine + (currentLine ? ' ' : '') + word;
    const metrics = ctx.measureText(testLine);
    
    if (metrics.width > canvas.width * 0.7 && currentLine) {
      lines.push(currentLine);
      currentLine = word;
    } else {
      currentLine = testLine;
    }
  }
  if (currentLine) lines.push(currentLine);
  
  // Dibujar lÃ­neas centradas
  const lineHeight = 40;
  const startY = canvas.height / 2 - (lines.length - 1) * lineHeight / 2;
  
  for (let i = 0; i < lines.length; i++) {
    ctx.fillText(lines[i], canvas.width / 2, startY + i * lineHeight);
  }
  
  // PequeÃ±a caja informativa arriba
  ctx.globalAlpha = 0.9;
  ctx.fillStyle = 'rgba(251, 191, 36, 0.9)';
  const boxWidth = 300;
  const boxHeight = 35;
  const boxX = (canvas.width - boxWidth) / 2;
  const boxY = 15;
  
  roundRect(ctx, boxX, boxY, boxWidth, boxHeight, 8);
  ctx.fill();
  
  // Texto de la caja
  ctx.fillStyle = '#92400e';
  ctx.font = 'bold 12px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  ctx.textAlign = 'center';
  ctx.textBaseline = 'middle';
  ctx.fillText('ðŸ’• Momento especial de Ana y Dani ðŸ’•', canvas.width / 2, boxY + boxHeight / 2);
  
  ctx.restore();
}

function drawIntro(ctx, canvas, lines, offset) {
  ctx.save();
  ctx.fillStyle = '#10b981'; // verde
  ctx.textAlign = 'center';
  ctx.font = 'bold 22px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  const centerX = canvas.width / 2;
  let y = canvas.height + offset;
  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    const scale = 1 - i * 0.05; // ligera perspectiva
    ctx.save();
    ctx.translate(centerX, y);
    ctx.scale(scale, scale);
    ctx.fillText(line, 0, 0);
    ctx.restore();
    y += 36;
  }
  ctx.restore();
}

function drawOverlay(ctx, canvas, title, subtitle) {
  ctx.save();
  ctx.fillStyle = 'rgba(0,0,0,0.45)';
  ctx.fillRect(0, 0, canvas.width, canvas.height);
  ctx.fillStyle = '#fff';
  ctx.font = 'bold 22px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  ctx.textAlign = 'center';
  ctx.fillText(title, canvas.width / 2, canvas.height / 2 - 6);
  ctx.font = '14px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  ctx.fillText(subtitle, canvas.width / 2, canvas.height / 2 + 18);
  ctx.restore();
}

function roundRect(ctx, x, y, w, h, r) {
  ctx.beginPath();
  ctx.moveTo(x + r, y);
  ctx.arcTo(x + w, y, x + w, y + h, r);
  ctx.arcTo(x + w, y + h, x, y + h, r);
  ctx.arcTo(x, y + h, x, y, r);
  ctx.arcTo(x, y, x + w, y, r);
  ctx.closePath();
}
