import React, { useEffect, useMemo, useRef, useState } from 'react';
import './Story.css';
import { supabase } from '../supabaseClient';

const Story = () => {
  const [milestones, setMilestones] = useState([]);
  const [loading, setLoading] = useState(true);
  const containerRef = useRef(null);

  useEffect(() => {
    const fetchMilestones = async () => {
      const { data, error } = await supabase
        .from('story_milestones')
        .select('*')
        .eq('is_published', true)
        .order('sort_order', { ascending: true });
      if (!error) setMilestones(data || []);
      setLoading(false);
    };
    fetchMilestones();
  }, []);

  const defaultMilestones = [
    { year: '2019', title: 'Nos conocimos', description: 'Fue en una noche de verano cuando nuestros caminos se cruzaron por primera vez...', image_url: '/images/img_story1.jpg', icon: 'üíï' },
    { year: '2021', title: 'Primera casa juntos', description: 'Decidimos dar el paso de convivir y crear nuestro primer hogar.', image_url: '/images/img_story2.jpg', icon: 'üè†' },
    { year: '2024', title: 'La propuesta', description: 'En un lugar muy especial para nosotros, lleg√≥ la pregunta m√°s importante.', image_url: '/images/img_story3.jpg', icon: 'üíç' },
    { year: '2026', title: '¬°Nos casamos!', description: 'Y aqu√≠ estamos, listos para celebrar nuestro amor con vosotros.', image_url: '/images/img_story4.jpg', icon: 'üéä' },
  ];

  // Scroll snap a pantalla completa
  const sections = useMemo(() => (milestones.length ? milestones : defaultMilestones), [milestones]);

  if (loading) {
    return (
      <div className="story-container story-loading">
        <div className="container">
          <h2>Cargando historia‚Ä¶</h2>
        </div>
      </div>
    );
  }

  return (
    <div className="story-container fullpage" ref={containerRef}>
      <section className="story-hero fullpage-section">
        <div className="container">
          <h1 className="story-title fade-in">Nuestra Historia</h1>
          <p className="story-subtitle fade-in">
            Cada historia de amor es √∫nica, y esta es la nuestra...
          </p>
        </div>
      </section>

      {sections.map((m, index) => (
        <section key={m.id || index} className={`timeline-full fullpage-section ${index % 2 === 0 ? 'left' : 'right'}`}>
          <div className="container">
            <div className={`timeline-full-content ${index % 2 === 0 ? 'slide-in-left' : 'slide-in-right'}`}>
              <div className="timeline-image">
                <img src={m.image_url || m.image} alt={m.title} />
                {m.icon && <div className="timeline-icon">{m.icon}</div>}
              </div>
              <div className="timeline-text">
                <div className="timeline-year">{m.year}</div>
                <h3>{m.title}</h3>
                <p>{m.description}</p>
              </div>
            </div>
          </div>
        </section>
      ))}

      <section className="love-message fullpage-section">
        <div className="container">
          <div className="love-content fade-in">
            <h2>Nuestro Amor</h2>
            <p>
              Despu√©s de todos estos a√±os juntos, hemos aprendido que el amor verdadero 
              no es solo encontrar a la persona perfecta, sino ver la perfecci√≥n en 
              una persona imperfecta. Hoy queremos celebrar con vosotros el inicio 
              de este nuevo cap√≠tulo en nuestras vidas.
            </p>
            <blockquote>
              "En cada historia de amor hay siempre algo de divino."
            </blockquote>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Story;
