import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import './Trivial.css';

const questions = [
  {
    question: '¿Dónde se conocieron Dani y Ana?',
    options: ['En la universidad', 'En una boda', 'En el trabajo', 'En un viaje'],
    answer: 0
  },
  {
    question: '¿Cuál es el destino favorito de la pareja?',
    options: ['Japón', 'París', 'Roma', 'Nueva York'],
    answer: 0
  },
  {
    question: '¿Qué día es la boda?',
    options: ['8 de Agosto, 2026', '15 de Julio, 2026', '1 de Septiembre, 2026', '25 de Diciembre, 2026'],
    answer: 0
  }
];

const Trivial = () => {
  const [step, setStep] = useState(0);
  const [score, setScore] = useState(0);
  const [selected, setSelected] = useState(null);
  const [name, setName] = useState('');
  const [finished, setFinished] = useState(false);
  const [ranking, setRanking] = useState([]);

  useEffect(() => {
    fetchRanking();
  }, [finished]);

  const fetchRanking = async () => {
    const { data } = await supabase
      .from('trivial_scores')
      .select('*')
      .order('score', { ascending: false })
      .limit(10);
    setRanking(data || []);
  };

  const handleAnswer = () => {
    if (selected === questions[step].answer) {
      setScore(score + 1);
    }
    setSelected(null);
    if (step < questions.length - 1) {
      setStep(step + 1);
    } else {
      setFinished(true);
    }
  };

  const handleSaveScore = async () => {
    if (name.trim()) {
      await supabase.from('trivial_scores').insert([
        { name, score }
      ]);
      fetchRanking();
    }
  };

  if (!finished) {
    return (
      <div className="trivial-container">
        <h2>Trivial: Nuestra Historia</h2>
        <div className="trivial-question">
          <h3>{questions[step].question}</h3>
          <ul>
            {questions[step].options.map((opt, idx) => (
              <li key={idx}>
                <button
                  className={selected === idx ? 'selected' : ''}
                  onClick={() => setSelected(idx)}
                  disabled={selected !== null}
                >
                  {opt}
                </button>
              </li>
            ))}
          </ul>
          {selected !== null && (
            <button className="next-btn" onClick={handleAnswer}>Siguiente</button>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="trivial-container">
      <h2>¡Trivial completado!</h2>
      <p>Tu puntuación: <strong>{score}</strong> / {questions.length}</p>
      <input
        type="text"
        placeholder="Tu nombre para el ranking"
        value={name}
        onChange={e => setName(e.target.value)}
        className="trivial-input"
      />
      <button className="save-btn" onClick={handleSaveScore}>Guardar puntuación</button>
      <h3>Ranking</h3>
      <ul className="ranking-list">
        {ranking.map((r, i) => (
          <li key={r.id}>
            <span>{i + 1}. {r.name}</span>
            <span>{r.score} pts</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Trivial;
