import React, { useEffect, useMemo, useRef, useState } from 'react';
import './Story.css';
import { supabase } from '../supabaseClient';

const Story = () => {
  const [milestones, setMilestones] = useState([]);
  const [loading, setLoading] = useState(true);
  const containerRef = useRef(null);

  useEffect(() => {
    const fetchMilestones = async () => {
      const { data, error } = await supabase
        .from('story_milestones')
        .select('*')
        .eq('is_published', true)
        .order('sort_order', { ascending: true });
      if (!error) setMilestones(data || []);
      setLoading(false);
    };
    fetchMilestones();
  }, []);

  // Scroll snap a pantalla completa
  const sections = useMemo(() => milestones, [milestones]);

  if (loading) {
    return (
      <div className="story-container story-loading">
        <div className="container">
          <h2>Cargando historia…</h2>
        </div>
      </div>
    );
  }

  return (
    <div className="story-container fullpage" ref={containerRef}>
      <section className="story-hero fullpage-section">
        <div className="container">
          <h1 className="story-title fade-in">Nuestra Historia</h1>
          <p className="story-subtitle fade-in">
            Cada historia de amor es única, y esta es la nuestra...
          </p>
        </div>
      </section>

      {sections.map((m, index) => (
        <section key={m.id || index} className={`timeline-full fullpage-section ${index % 2 === 0 ? 'left' : 'right'}`}>
          <div className="container">
            <div className="timeline-full-content">
              <div className="timeline-image">
                <img src={m.image_url || m.image} alt={m.title} />
                {m.icon && <div className="timeline-icon">{m.icon}</div>}
              </div>
              <div className="timeline-text">
                <div className="timeline-year">{m.year}</div>
                <h3>{m.title}</h3>
                <p>{m.description}</p>
              </div>
            </div>
          </div>
        </section>
      ))}

      <section className="love-message fullpage-section">
        <div className="container">
          <div className="love-content fade-in">
            <h2>Nuestro Amor</h2>
            <p>
              Después de todos estos años juntos, hemos aprendido que el amor verdadero 
              no es solo encontrar a la persona perfecta, sino ver la perfección en 
              una persona imperfecta. Hoy queremos celebrar con vosotros el inicio 
              de este nuevo capítulo en nuestras vidas.
            </p>
            <blockquote>
              "En cada historia de amor hay siempre algo de divino."
            </blockquote>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Story;
