import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './MainLayout.css';

const MainLayout = ({ children, onLogout }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const location = useLocation();
  // Modo oscuro y estado de scroll
  const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'light');
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    document.documentElement.setAttribute('data-theme', theme);
    localStorage.setItem('theme', theme);
  }, [theme]);

  useEffect(() => {
    const onScroll = () => setIsScrolled(window.scrollY > 10);
    window.addEventListener('scroll', onScroll);
    onScroll();
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  const toggleTheme = () => setTheme(prev => (prev === 'light' ? 'dark' : 'light'));

  const isActive = (path) => {
    return location.pathname === path || 
           (path === '/' && location.pathname === '/home');
  };

  const menuItems = [
    { path: '/', label: 'Inicio', icon: 'fas fa-home' },
    { path: '/story', label: 'Nuestra Historia', icon: 'fas fa-heart' },
    { path: '/details', label: 'Detalles', icon: 'fas fa-calendar' },
    { path: '/gallery', label: 'GalerÃ­a', icon: 'fas fa-images' },
    { path: '/accommodation', label: 'Alojamiento', icon: 'fas fa-bed' },
    { path: '/rsvp', label: 'Confirmar', icon: 'fas fa-envelope' },
    { path: '/trivial', label: 'Trivial', icon: 'fas fa-star' },
    { path: '/faq', label: 'FAQs', icon: 'fas fa-question-circle' },
    { path: '/contact', label: 'Contacto', icon: 'fas fa-phone' }
  ];

  return (
    <div className="main-layout">
      {/* Header */}
      <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
        <div className="header-content">
          <div className="logo-section">
            <Link to="/" className="logo-link" onClick={closeMenu}>
              <img 
                src="/images/logo boda.png" 
                alt="Dani & Ana" 
                className="header-logo"
              />
              <div className="logo-text">
                <span className="logo-names">Dani & Ana</span>
                <span className="logo-date">08.08.2026</span>
              </div>
            </Link>
          </div>

          {/* Desktop Navigation */}
          <nav className="desktop-nav">
            {menuItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`nav-link ${isActive(item.path) ? 'active' : ''}`}
              >
                <i className={item.icon}></i>
                {item.label}
              </Link>
            ))}
            <button onClick={toggleTheme} className="theme-toggle" aria-label="Cambiar tema">
              <i className={theme === 'light' ? 'fas fa-moon' : 'fas fa-sun'}></i>
            </button>
            <button onClick={onLogout} className="logout-btn">
              <i className="fas fa-sign-out-alt"></i>
              Salir
            </button>
          </nav>

          {/* Mobile Menu Button */}
          <button 
            className={`mobile-menu-btn ${isMenuOpen ? 'active' : ''}`}
            onClick={toggleMenu}
            aria-label="Abrir menÃº"
          >
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
      </header>

      {/* Mobile Navigation */}
      <nav className={`mobile-nav ${isMenuOpen ? 'open' : ''}`}>
        <div className="mobile-nav-overlay" onClick={closeMenu}></div>
        <div className="mobile-nav-content">
          <div className="mobile-nav-header">
            <h3>MenÃº</h3>
            <button onClick={closeMenu} className="close-btn">
              <i className="fas fa-times"></i>
            </button>
          </div>
          <div className="mobile-nav-links">
            {menuItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`mobile-nav-link ${isActive(item.path) ? 'active' : ''}`}
                onClick={closeMenu}
              >
                <i className={item.icon}></i>
                {item.label}
              </Link>
            ))}
            <button onClick={toggleTheme} className="mobile-nav-link" aria-label="Cambiar tema">
              <i className={theme === 'light' ? 'fas fa-moon' : 'fas fa-sun'}></i>
              {theme === 'light' ? 'Modo oscuro' : 'Modo claro'}
            </button>
            <button onClick={onLogout} className="mobile-logout-btn">
              <i className="fas fa-sign-out-alt"></i>
              Cerrar SesiÃ³n
            </button>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main className="main-content">
        {children}
      </main>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-section">
            <h4>Dani & Ana</h4>
            <p>8 de Agosto, 2026</p>
          </div>
          <div className="footer-section">
            <h4>Contacto</h4>
            <p>ðŸ“§ info@daniyanaswedding.com</p>
            <p>ðŸ“± +34 123 456 789</p>
          </div>
          <div className="footer-section">
            <h4>SÃ­guenos</h4>
            <div className="social-links">
              <a href="#" aria-label="Instagram">
                <i className="fab fa-instagram"></i>
              </a>
              <a href="#" aria-label="Facebook">
                <i className="fab fa-facebook"></i>
              </a>
              <a href="#" aria-label="WhatsApp">
                <i className="fab fa-whatsapp"></i>
              </a>
            </div>
          </div>
        </div>
        <div className="footer-bottom">
          <p>&copy; 2026 Dani & Ana Wedding. Con ðŸ’• para nuestros invitados.</p>
        </div>
      </footer>
    </div>
  );
};

export default MainLayout;
