import React, { useEffect, useRef, useState } from 'react';

// Minijuego 2D: El novio quiere llegar a la iglesia saltando obstÃ¡culos
// Controles: Space o Flecha â†‘ para saltar. R para reiniciar. Toque/Tap en mÃ³vil para saltar.
export default function Game() {
  const canvasRef = useRef(null);
  const [status, setStatus] = useState('playing'); // playing | win | gameover
  const [distancePct, setDistancePct] = useState(0);

  // ConfiguraciÃ³n del mundo
  const WORLD = useRef({
    width: 3800, // longitud del nivel (px)
    speed: 250, // px/seg
    gravity: 1600, // px/seg^2
    jumpV: 600, // velocidad inicial del salto
    groundY: 220,
    player: { x: 80, y: 0, w: 32, h: 42, vy: 0, onGround: true },
    worldX: 0, // desplazamiento del mundo
    churchX: 3600,
    obstacles: [],
    lastTs: 0,
  });

  // Crear obstÃ¡culos fijos a lo largo del mundo
  useEffect(() => {
    const obs = [];
    const pattern = [
      350, 600, 880, 1200, 1500, 1690, 1950, 2250, 2480, 2700, 3000, 3200, 3400,
    ];
    for (let i = 0; i < pattern.length; i++) {
      const x = pattern[i] + Math.random() * 40 - 20;
      const w = 24 + Math.random() * 10;
      const h = 26 + Math.random() * 14;
      obs.push({ x, y: WORLD.current.groundY - h, w, h });
    }
    WORLD.current.obstacles = obs;
    // posiciÃ³n inicial del jugador en el suelo
    WORLD.current.player.y = WORLD.current.groundY - WORLD.current.player.h;
  }, []);

  // Bucle de juego y renderizado
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext('2d');

    let rafId;
    const loop = (ts) => {
      if (!WORLD.current.lastTs) WORLD.current.lastTs = ts;
      const dt = Math.min(0.032, (ts - WORLD.current.lastTs) / 1000); // limitar dt
      WORLD.current.lastTs = ts;

      update(dt);
      render(ctx, canvas);
      rafId = requestAnimationFrame(loop);
    };

    rafId = requestAnimationFrame(loop);
    return () => cancelAnimationFrame(rafId);
  }, [status]);

  // Entradas del usuario
  useEffect(() => {
    const onKey = (e) => {
      if (e.repeat) return;
      if ((e.code === 'Space' || e.code === 'ArrowUp') && status === 'playing') jump();
      if (e.code === 'KeyR') reset();
    };
    const onClick = () => { if (status === 'playing') jump(); };

    window.addEventListener('keydown', onKey);
    window.addEventListener('touchstart', onClick, { passive: true });
    window.addEventListener('mousedown', onClick);
    return () => {
      window.removeEventListener('keydown', onKey);
      window.removeEventListener('touchstart', onClick);
      window.removeEventListener('mousedown', onClick);
    };
  }, [status]);

  const jump = () => {
    const p = WORLD.current.player;
    if (p.onGround) {
      p.vy = -WORLD.current.jumpV;
      p.onGround = false;
    }
  };

  const reset = () => {
    setStatus('playing');
    WORLD.current.worldX = 0;
    WORLD.current.lastTs = 0;
    WORLD.current.player = { x: 80, y: WORLD.current.groundY - 42, w: 32, h: 42, vy: 0, onGround: true };
    setDistancePct(0);
  };

  const update = (dt) => {
    if (status !== 'playing') return;
    const w = WORLD.current;
    const p = w.player;

    // Avance del mundo
    w.worldX += w.speed * dt;

    // FÃ­sica del jugador
    p.vy += w.gravity * dt;
    p.y += p.vy * dt;

    // Suelo
    const groundY = w.groundY - p.h;
    if (p.y >= groundY) {
      p.y = groundY;
      p.vy = 0;
      p.onGround = true;
    }

    // Colisiones con obstÃ¡culos visibles
    const viewMin = w.worldX - 80;
    const viewMax = w.worldX + 820;
    for (const o of w.obstacles) {
      if (o.x + o.w < viewMin || o.x > viewMax) continue;
      const px1 = p.x, px2 = p.x + p.w;
      const py1 = p.y, py2 = p.y + p.h;
      const ox1 = o.x - w.worldX + p.x; // convertir a coords de pantalla con base en jugador
      const ox2 = ox1 + o.w;
      const oy1 = o.y, oy2 = o.y + o.h;
      if (px1 < ox2 && px2 > ox1 && py1 < oy2 && py2 > oy1) {
        setStatus('gameover');
      }
    }

    // Llegada a la iglesia
    if (w.worldX + p.x >= w.churchX) {
      setStatus('win');
    }

    // Progreso
    setDistancePct(Math.min(100, Math.round(((w.worldX + p.x) / w.churchX) * 100)));
  };

  const render = (ctx, canvas) => {
    const w = WORLD.current;
    // Fondo
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // Cielo degradado
    const g = ctx.createLinearGradient(0, 0, 0, canvas.height);
    g.addColorStop(0, '#e8f0ff');
    g.addColorStop(1, '#ffffff');
    ctx.fillStyle = g;
    ctx.fillRect(0, 0, canvas.width, canvas.height);

    // Suelo
    ctx.fillStyle = '#c7d2fe';
    ctx.fillRect(0, w.groundY + 10, canvas.width, 4);

    // Iglesias (meta)
    const churchScreenX = w.churchX - w.worldX + 80; // base en x del jugador
    drawChurch(ctx, churchScreenX, w.groundY - 90);

    // ObstÃ¡culos
    for (const o of w.obstacles) {
      const sx = o.x - w.worldX + 80;
      if (sx < -60 || sx > canvas.width + 60) continue;
      drawObstacle(ctx, sx, o.y, o.w, o.h);
    }

    // Jugador (novio)
    drawGroom(ctx, 80, w.player.y, w.player.w, w.player.h, !w.player.onGround);

    // HUD
    ctx.fillStyle = '#334155';
    ctx.font = '14px Inter, system-ui, -apple-system, Segoe UI, Roboto';
    ctx.fillText(`Progreso: ${distancePct}%`, 12, 18);

    if (status === 'gameover') {
      drawOverlay(ctx, canvas, 'Â¡Ups! Te has tropezado', 'Pulsa R o toca para reiniciar');
    } else if (status === 'win') {
      drawOverlay(ctx, canvas, 'Â¡Llegaste a la iglesia! ðŸ’’', 'Pulsa R o toca para jugar de nuevo');
    }
  };

  return (
    <div style={{ maxWidth: 860, margin: '0 auto', padding: '1rem' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '.5rem' }}>Camino a la iglesia</h2>
      <p style={{ textAlign: 'center', marginTop: 0, color: '#64748b' }}>
        Salta los obstÃ¡culos para llegar a la iglesia. Controles: Espacio / â†‘ o toca la pantalla.
      </p>
      <div style={{ borderRadius: 12, overflow: 'hidden', boxShadow: '0 6px 24px rgba(0,0,0,.12)', background: 'white' }}>
        <canvas
          ref={canvasRef}
          width={800}
          height={260}
          style={{ width: '100%', display: 'block', background: 'linear-gradient(#eef2ff, #fff)' }}
        />
      </div>
    </div>
  );
}

function drawObstacle(ctx, x, y, w, h) {
  ctx.save();
  ctx.fillStyle = '#94a3b8';
  ctx.strokeStyle = '#475569';
  ctx.lineWidth = 2;
  roundRect(ctx, x, y, w, h, 6);
  ctx.fill();
  ctx.stroke();
  ctx.restore();
}

function drawGroom(ctx, x, y, w, h, jumping) {
  ctx.save();
  // cuerpo
  ctx.fillStyle = '#1f2937';
  roundRect(ctx, x, y, w, h, 8);
  ctx.fill();
  // cabeza
  ctx.fillStyle = '#fde68a';
  ctx.beginPath();
  ctx.arc(x + w / 2, y - 8, 10, 0, Math.PI * 2);
  ctx.fill();
  // corbata
  ctx.fillStyle = '#ef4444';
  ctx.fillRect(x + w / 2 - 3, y + 10, 6, 10);
  // sombrita al saltar
  if (jumping) {
    ctx.globalAlpha = 0.2;
    ctx.fillStyle = '#000';
    ctx.beginPath();
    ctx.ellipse(x + w / 2, y + h + 10, w * 0.6, 4, 0, 0, Math.PI * 2);
    ctx.fill();
    ctx.globalAlpha = 1;
  }
  ctx.restore();
}

function drawChurch(ctx, x, y) {
  ctx.save();
  // base
  ctx.fillStyle = '#fde68a';
  roundRect(ctx, x, y, 90, 70, 8);
  ctx.fill();
  // puerta
  ctx.fillStyle = '#7c3aed';
  roundRect(ctx, x + 34, y + 30, 22, 40, 6);
  ctx.fill();
  // torre
  ctx.fillStyle = '#facc15';
  roundRect(ctx, x + 60, y - 26, 22, 26, 6);
  ctx.fill();
  // cruz
  ctx.fillStyle = '#dc2626';
  ctx.fillRect(x + 70, y - 34, 4, 16);
  ctx.fillRect(x + 64, y - 30, 16, 4);
  ctx.restore();
}

function drawOverlay(ctx, canvas, title, subtitle) {
  ctx.save();
  ctx.fillStyle = 'rgba(0,0,0,0.45)';
  ctx.fillRect(0, 0, canvas.width, canvas.height);
  ctx.fillStyle = '#fff';
  ctx.font = 'bold 22px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  ctx.textAlign = 'center';
  ctx.fillText(title, canvas.width / 2, canvas.height / 2 - 6);
  ctx.font = '14px Inter, system-ui, -apple-system, Segoe UI, Roboto';
  ctx.fillText(subtitle, canvas.width / 2, canvas.height / 2 + 18);
  ctx.restore();
}

function roundRect(ctx, x, y, w, h, r) {
  ctx.beginPath();
  ctx.moveTo(x + r, y);
  ctx.arcTo(x + w, y, x + w, y + h, r);
  ctx.arcTo(x + w, y + h, x, y + h, r);
  ctx.arcTo(x, y + h, x, y, r);
  ctx.arcTo(x, y, x + w, y, r);
  ctx.closePath();
}
