import React, { useState } from 'react';
import { supabase } from '../supabaseClient';
import './RSVP.css';

const RSVP = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    attendance: '', // 'yes' | 'no'
    guests: 1,
    message: '',
    allergies: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    if (!formData.attendance) {
      setError('Selecciona si asistirás.');
      return;
    }
    setIsSubmitting(true);
    try {
      const payload = {
        status: formData.attendance === 'yes' ? 'confirmado' : 'no',
        guests_count: Number(formData.guests) || 1,
        message: formData.message || '',
        allergies: formData.allergies || ''
      };
      const { error: dbError } = await supabase.from('rsvp').insert([payload]);
      if (dbError) throw dbError;
      setIsSubmitted(true);
    } catch (err) {
      setError('No se pudo guardar tu confirmación. Inténtalo de nuevo.');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (isSubmitted) {
    return (
      <div className="rsvp-container">
        <section className="rsvp-hero">
          <h2>¡Gracias por tu respuesta! 💌</h2>
          <p>Hemos registrado tu confirmación. ¡Nos vemos muy pronto!</p>
        </section>
      </div>
    );
  }

  return (
    <div className="rsvp-container">
      <section className="rsvp-hero">
        <h2>Confirmar asistencia</h2>
        <p>Cuéntanos si podrás acompañarnos en nuestro gran día.</p>
      </section>

      <section className="rsvp-form-section">
        <div className="container">
          <form onSubmit={handleSubmit} className="rsvp-form">
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="name">Nombre completo</label>
                <input id="name" name="name" type="text" value={formData.name} onChange={handleChange} />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input id="email" name="email" type="email" value={formData.email} onChange={handleChange} />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="phone">Teléfono</label>
                <input id="phone" name="phone" type="tel" value={formData.phone} onChange={handleChange} />
              </div>
              <div className="form-group">
                <label>¿Asistirás?</label>
                <div className="radio-group">
                  <label>
                    <input type="radio" name="attendance" value="yes" checked={formData.attendance === 'yes'} onChange={handleChange} />
                    Sí
                  </label>
                  <label>
                    <input type="radio" name="attendance" value="no" checked={formData.attendance === 'no'} onChange={handleChange} />
                    No
                  </label>
                </div>
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="guests">Número de acompañantes</label>
                <input id="guests" name="guests" type="number" min="1" value={formData.guests} onChange={handleChange} />
              </div>
              <div className="form-group">
                <label htmlFor="allergies">Intolerancias alimenticias</label>
                <input id="allergies" name="allergies" type="text" placeholder="Ej: gluten, lactosa, frutos secos..." value={formData.allergies} onChange={handleChange} />
              </div>
            </div>
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="message">Mensaje</label>
                <input id="message" name="message" type="text" value={formData.message} onChange={handleChange} />
              </div>
            </div>

            {error && <div className="rsvp-error">{error}</div>}

            <button type="submit" className="btn btn-primary" disabled={isSubmitting}>
              {isSubmitting ? 'Enviando...' : 'Enviar confirmación'}
            </button>
          </form>
        </div>
      </section>
    </div>
  );
};

export default RSVP;
