import React from 'react';
import './Accommodation.css';

const Accommodation = () => {
  const hotels = [
    {
      id: 1,
      name: "Hotel Mondariz Balneario",
      destacado: true,
      descripcion: "El hotel principal de la boda. Spa, piscina, desayuno y parking incluidos. Precio especial para invitados.",
      distancia: "En el mismo recinto de la celebración",
      precio: "Desde 120€/noche",
      telefono: "+34 986 656 156",
      web: "https://www.mondarizbalneario.com",
      imagen: "/images/img_story1.jpg",
      descuento: "Código: BODADANIANA2026"
    },
    {
      id: 2,
      name: "Hotel Villa Termal Caldas",
      destacado: false,
      descripcion: "Hotel con aguas termales y ambiente tranquilo. Ideal para relajarse antes/después de la boda.",
      distancia: "18 km (20 min en coche)",
      precio: "Desde 80€/noche",
      telefono: "+34 986 540 000",
      web: "",
      imagen: "/images/img_story2.jpg",
      descuento: ""
    },
    {
      id: 3,
      name: "Parador de Pontevedra",
      destacado: false,
      descripcion: "Parador histórico en el centro de Pontevedra. Perfecto para explorar la ciudad.",
      distancia: "25 km (30 min en coche)",
      precio: "Desde 95€/noche",
      telefono: "+34 986 855 800",
      web: "https://www.parador.es/es/paradores/parador-de-pontevedra",
      imagen: "/images/img_story3.jpg",
      descuento: ""
    },
    {
      id: 4,
      name: "Casas Rurales y Airbnb",
      destacado: false,
      descripcion: "Opciones económicas y familiares en la zona. Ideal para grupos grandes.",
      distancia: "15-25 km",
      precio: "Desde 45€/noche",
      telefono: "",
      web: "https://www.airbnb.es/s/Mondariz-Balneario",
      imagen: "/images/img_story4.jpg",
      descuento: ""
    }
  ];

  const handleReservation = (hotel) => {
    const message = `Hola, me gustaría hacer una reserva para la boda de Dani y Ana (8 de Agosto, 2026) en ${hotel.name}. Por favor, apliquen el descuento especial.`;
    const whatsapp = `https://wa.me/34123456789?text=${encodeURIComponent(message)}`;
    window.open(whatsapp, '_blank');
  };

  return (
    <div className="accommodation-page">
      <header className="accommodation-header minimal">
        <h1 className="accommodation-title">
          <i className="fas fa-bed"></i> Alojamiento
        </h1>
        <p className="accommodation-subtitle">Opciones para tu estancia cerca de la boda. Reserva pronto, agosto es temporada alta.</p>
      </header>
      <main className="accommodation-content minimal">
        <section className="info-section minimal">
          <div className="info-card minimal">
            <ul className="info-list">
              <li><i className="fas fa-calendar-alt"></i> <strong>Fechas recomendadas:</strong> 7-9 de agosto 2026</li>
              <li><i className="fas fa-tag"></i> <strong>Descuentos:</strong> Menciona "Boda Dani&Ana" al reservar</li>
              <li><i className="fas fa-clock"></i> <strong>Reserva pronto:</strong> Los hoteles se llenan rápido</li>
            </ul>
          </div>
        </section>
        <section className="hotels-section minimal">
          <h2 className="section-title minimal">Hoteles y alojamientos</h2>
          <div className="hotels-grid minimal">
            {hotels.map(hotel => (
              <div key={hotel.id} className={`hotel-card minimal${hotel.destacado ? ' destacado' : ''}`}>
                <div className="hotel-image minimal">
                  <img src={hotel.imagen} alt={hotel.name} />
                  {hotel.destacado && <span className="hotel-recommended">Recomendado</span>}
                </div>
                <div className="hotel-content minimal">
                  <h3 className="hotel-name minimal">{hotel.name}</h3>
                  <p className="hotel-description minimal">{hotel.descripcion}</p>
                  <div className="hotel-details minimal">
                    <span><i className="fas fa-map-marker-alt"></i> {hotel.distancia}</span>
                    <span><i className="fas fa-euro-sign"></i> {hotel.precio}</span>
                  </div>
                  {hotel.descuento && (
                    <div className="discount-banner minimal">
                      <i className="fas fa-gift"></i> {hotel.descuento}
                    </div>
                  )}
                  <div className="hotel-actions minimal">
                    {hotel.telefono && (
                      <a href={`tel:${hotel.telefono}`} className="phone-link minimal">
                        <i className="fas fa-phone"></i> {hotel.telefono}
                      </a>
                    )}
                    {hotel.web && (
                      <a href={hotel.web} target="_blank" rel="noopener noreferrer" className="website-link minimal">
                        <i className="fas fa-globe"></i> Sitio web
                      </a>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
};

export default Accommodation;
